import React from "react";
import { connect } from "react-redux";
import { firestoreConnect } from "react-redux-firebase";
import { compose } from "redux";

const ProjectDetails = props => {
  const { project } = props;
  if (project) {
    return (
      <div className="container section project-details">
        <div className="card z-depth-0">
          <div className="card-content">
            <span className="card-title">{project.title}</span>
            <div className="projectDetails">
              {project.content}
              <br /> Starting Date: {project.startingDate},
              <br /> Finnisghing Date: {project.finnishingDate},
              <br />
              Product Name:{project.productName}, <br />
              Product Description: {project.productDescription}
              <br />
              Product Number: {project.productNumber},
              <br />
              Area Name: {project.areaName},
              <br />
              Area Height: {project.height},
              <br />
              Area Width:{project.width},
              <br />
              Total Sqaure ft: {project.totalSqft}
            </div>
            {/* <p>Starting Date: {project.startingDate}</p>
            <p>Finnisghing Date: {project.finnishingDate}</p>
            <h5>Product</h5>
            <p>Product Name:{project.productName}</p>
            <p>Product Description: {project.productDescription}</p>
            <p>Product Number: {project.productNumber}</p>
            <h5>Area</h5>
            <p>Area Name: {project.areaName}</p>
            <p>Area Height: {project.height}</p>
            <p>Area Width:{project.width}</p> */}

            {/* <p>Total Sqaure ft: {project.totalSqft}</p> */}
          </div>
          <div className="card-action grey lighten-4 grey-text">
            <div>
              Posted by {project.userFirstName} {project.userLastName}
            </div>
            <div>2nd September, 2am</div>
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div className="container center">
        <p>Loading project...</p>
      </div>
    );
  }
};

const mapStateToProps = (state, ownProps) => {
  console.log(state);
  const id = ownProps.match.params.id;
  const projects = state.firestore.data.projects;
  const project = projects ? projects[id] : null;
  return {
    project: project
  };
};

export default compose(
  connect(mapStateToProps),
  firestoreConnect([
    {
      collection: "projects"
    }
  ])
)(ProjectDetails);
